import 'package:flutter/material.dart';class HomeScreen extends StatelessWidget {  const HomeScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          ElevatedButton(            onPressed: () {              Navigator.push(                context,                MaterialPageRoute(                    builder: (context) =>                        NewScreen(title: 'Elevated Button Page')),              );            },            child: Text('Elevated Button'),          ),          SizedBox(height: 20),          TextButton(            onPressed: () {              showDialog(                context: context,                builder: (context) => AlertDialog(                  title: Text('Text Button Pressed'),                  content: Text('You tapped the Text Button.'),                  actions: [                    TextButton(                      onPressed: () => Navigator.pop(context),                      child: Text('Close'),                    ),                  ],                ),              );            },            child: Text('Text Button'),          ),          SizedBox(height: 20),          OutlinedButton(            onPressed: () {              Navigator.push(                context,                MaterialPageRoute(                    builder: (context) =>                        NewScreen(title: 'Outlined Button Page')),              );            },            child: Text('Outlined Button'),          ),        ],      )),      floatingActionButton: FloatingActionButton(        onPressed: () {          showDialog(            context: context,            builder: (context) => AlertDialog(              title: Text('FAB Pressed'),              content: Text('FloatingActionButton tapped.'),              actions: [                TextButton(                  onPressed: () => Navigator.pop(context),                  child: Text('Close'),                ),              ],            ),          );        },        child: Icon(Icons.add),      ),    );  }}class NewScreen extends StatelessWidget {  final String title;  const NewScreen({super.key, required this.title});  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: Text(          'Welcome to $title!',          style: TextStyle(fontSize: 24),        ),      ),    );  }}class ButtonStateScreen extends StatefulWidget {  const ButtonStateScreen({super.key});  @override  State<ButtonStateScreen> createState() => _ButtonStateScreenState();}class _ButtonStateScreenState extends State<ButtonStateScreen> {  bool isFollowing = false;  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: ElevatedButton(          onPressed: () {            setState(() {              isFollowing = !isFollowing;            });          },          style: ElevatedButton.styleFrom(            backgroundColor: isFollowing ? Colors.green : Colors.blue,          ),          child: Text(isFollowing ? 'Unfollow' : 'Follow'),        ),      ),    );  }}