import 'package:flutter/material.dart';class IconDisplayScreen extends StatelessWidget {  const IconDisplayScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(      ),      body: Center(child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          IconWithLabel(icon: Icons.home, label: 'Home', color: Colors.blue, size: 40),          SizedBox(height: 20),          IconWithLabel(icon: Icons.favorite, label: 'Favorite', color: Colors.red, size: 50),          SizedBox(height: 20),          IconWithLabel(icon: Icons.star, label: 'Star', color: Colors.yellow, size: 60),          SizedBox(height: 20),          IconWithLabel(icon: Icons.settings, label: 'Settings', color: Colors.grey, size: 45),          SizedBox(height: 20),          IconWithLabel(icon: Icons.phone, label: 'Phone', color: Colors.green, size: 50),        ],      )),      floatingActionButton: FloatingActionButton(        onPressed: () {          Navigator.push(            context,            MaterialPageRoute(builder: (context) => NewScreen()),          );        },        child: Icon(Icons.navigation),      ),    );  }}class IconWithLabel extends StatelessWidget {  final IconData icon;  final String label;  final Color color;  final double size;  const IconWithLabel({super.key,    required this.icon,    required this.label,    required this.color,    required this.size,  });  @override  Widget build(BuildContext context) {    return Column(      children: [        Icon(icon, color: color, size: size),        Text(label, style: TextStyle(fontSize: 16)),      ],    );  }}class NewScreen extends StatelessWidget {  const NewScreen({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('New Screen'),      ),      body: Center(        child: Text(          'Welcome to the New Screen!',          style: TextStyle(fontSize: 24),        ),      ),    );  }}