import 'package:flutter/material.dart';class ImageWidget extends StatelessWidget {  const ImageWidget({    super.key,  });  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(        child: Center(          child: Column(            spacing: 30,            children: [              LocalImage(),              DecoratedImage(),              ImageWithRoundedCorners(),            ],          ),        ),      ),    );  }}class LocalImage extends StatelessWidget {  const LocalImage({super.key});  @override  Widget build(BuildContext context) {    return SizedBox(      width: 200,      height: 200,      child: Image.asset('Assests/images/images.png'),    );  }}class DecoratedImage extends StatelessWidget {  const DecoratedImage({super.key});  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        border: Border.all(color: Colors.blue, width: 2),        boxShadow: [          BoxShadow(            color: Colors.black26,            blurRadius: 5,            offset: Offset(2, 2),          ),        ],      ),      child: Image.asset(        'Assests/images/images.png',        width: 200,        height: 200,      ),    );  }}class ImageWithRoundedCorners extends StatelessWidget {  const ImageWithRoundedCorners({super.key});  @override  Widget build(BuildContext context) {    return ClipRRect(      borderRadius: BorderRadius.circular(20),      child: Image.asset(        'Assests/images/images.png',        width: 200,        height: 200,        fit: BoxFit.cover,      ),    );  }}