import 'package:flutter/material.dart';class ListViewExample extends StatelessWidget {  const ListViewExample({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(            body: SafeArea(          child: Column(        children: [          Expanded(            child: BasicListView(),          ),          SizedBox(            height: 120,            child: HorizontalListView(),          ),          SizedBox(height: 20),          Expanded(            child: ListWithImages(),          ),        ],      )),    );  }}class BasicListView extends StatelessWidget {  const BasicListView({super.key});  @override  Widget build(BuildContext context) {    final List<String> items = [      'Apple',      'Banana',      'Cherry',      'Date',      'Elderberry',      'Fig',      'Grapes',      'Honeydew',      'Indian Fig',      'Jack fruit'    ];    return ListView.builder(      itemCount: items.length,      itemBuilder: (context, index) {        return ListTile(          title: Text(items[index]),        );      },    );  }}class ListWithImages extends StatelessWidget {  const ListWithImages({super.key});  @override  Widget build(BuildContext context) {    final List<Map<String, String>> items2 = [      {'image': 'https://via.placeholder.com/100', 'text': 'Item 1'},      {'image': 'https://via.placeholder.com/100', 'text': 'Item 2'},      {'image': 'https://via.placeholder.com/100', 'text': 'Item 3'},      {'image': 'https://via.placeholder.com/100', 'text': 'Item 4'},      {'image': 'https://via.placeholder.com/100', 'text': 'Item 5'},    ];    return ListView.builder(      itemCount: items2.length,      itemBuilder: (context, index) {        return ListTile(          leading: Image.network(            items2[index]['image']!,            width: 50,            height: 50,            fit: BoxFit.cover,          ),          title: Text(items2[index]['text']!),        );      },    );  }}class HorizontalListView extends StatelessWidget {  const HorizontalListView({super.key});  @override  Widget build(BuildContext context) {    final List<String> featuredItems = [      'Featured 1',      'Featured 2',      'Featured 3',      'Featured 4',      'Featured 5',    ];    return ListView.builder(      scrollDirection: Axis.horizontal,      itemCount: featuredItems.length,      itemBuilder: (context, index) {        return Container(          width: 100,          margin: EdgeInsets.symmetric(horizontal: 8),          decoration: BoxDecoration(            border: Border.all(color: Colors.blue),            borderRadius: BorderRadius.circular(8),            color: Colors.blue[50],          ),          child: Center(            child: Text(featuredItems[index]),          ),        );      },    );  }}