import 'package:flutter/material.dart';class RowAndCo extends StatelessWidget {  const RowAndCo({super.key});  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(        child: Center(            child: Column(              mainAxisAlignment: MainAxisAlignment.center,          spacing: 100,          children: [            BasicLayout(),            AlignmentAndSpacing(),          ],        )),      ),    );  }}class BasicLayout extends StatelessWidget {  const BasicLayout({super.key});  @override  Widget build(BuildContext context) {    return Column(      children: [        Row(          mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            Text("Item 1"),            Text("Item 2"),            Text("Item 3"),          ],        ),        SizedBox(height: 20),        Row(          mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            Icon(Icons.star, size: 30),            Icon(Icons.favorite, size: 30),            Icon(Icons.home, size: 30),          ],        ),      ],    );  }}class AlignmentAndSpacing extends StatelessWidget {  const AlignmentAndSpacing({super.key});  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Row(          mainAxisAlignment: MainAxisAlignment.spaceEvenly,          children: [            ElevatedButton(onPressed: () {}, child: Text("Button 1")),            ElevatedButton(onPressed: () {}, child: Text("Button 2")),          ],        ),        SizedBox(height: 20),        Row(          mainAxisAlignment: MainAxisAlignment.spaceAround,          children: [            ElevatedButton(onPressed: () {}, child: Text("Button 3")),            ElevatedButton(onPressed: () {}, child: Text("Button 4")),            ElevatedButton(onPressed: () {}, child: Text("Button 5")),          ],        ),        SizedBox(height: 20),        Row(          mainAxisAlignment: MainAxisAlignment.start,          children: [            Padding(              padding: const EdgeInsets.all(8.0),              child: ElevatedButton(onPressed: () {}, child: Text("Button 6")),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: ElevatedButton(onPressed: () {}, child: Text("Button 7")),            ),          ],        ),      ],    );  }}